export function getLoggedUser(id){
  const options = {
    method: "GET",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  return fetch("http://localhost:8080/usuarios/todos-os-usuarios/"+id, options).then(response => response.json());
}
