import { createNewUser } from "./authentication";

const userId = "7227ca64-8e82-4054-babb-42db7599613f";

// createNewUser({
//   "email": "mateusbrito@email.com",
//   "password": "123456"
// });

export function getLoggedUserId(){
  return userId;
}

export function getLoggedUser(){
  const options = {
    method: "GET",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  return fetch("http://localhost:8080/usuarios/todos-os-usuarios/"+userId, options).then(response => response.json());
}

export function postNewClient(newClient){
  const options = {
    method: "POST",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newClient)
  }

  fetch("http://localhost:8080/clientes/criar-cliente", options);
}

export function deleteClient(id){
  const options = {
    method: "DELETE",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  fetch("http://localhost:8080/clientes/deletar-cliente/"+id, options);
}
