import { createNewUser } from "./authentication";

const userId = "7227ca64-8e82-4054-babb-42db7599613f";

export function postNewUser(newUser){
  createNewUser(newUser)
    .then((data) => {
      newUser.firebaseId = data.user.uid;

      console.log(newUser);
