import axios from "axios";
import { createNewUser, getUserFirebaseId } from "./authentication";

let userId;

export async function setUserId(id){
  userId = id;
}

export async function getUserId(){
  return userId;
}

export function postNewUser(newUser){
  createNewUser(newUser)
    .then((data) => {
      newUser.firebaseId = data.user.uid;

      const options = {
        method: "POST",
        headers: {
          "Authorization": "Basic YWRtaW46YWRtaW4=",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(newUser)
      }

      fetch("http://localhost:8080/usuarios/criar-usuario", options);
    });
}

export function getLoggedUser(){
  const options = {
    method: "GET",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  return getUserFirebaseId()
    .then(data => {
      return fetch("http://localhost:8080/usuarios/login/todos-os-usuarios/"+data, options)
        .then(response => response.json());
    });
}

export function postNewClient(newClient){
  const options = {
    method: "POST",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newClient)
  }

  fetch("http://localhost:8080/clientes/criar-cliente", options);
}

export function deleteClient(id){
  const options = {
    method: "DELETE",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  fetch("http://localhost:8080/clientes/deletar-cliente/"+id, options);
}
