import { useState, useContext } from 'react';
import './profile.css';
import Header from '../../components/Header';
import Title from '../../components/Title';
import { AuthContext } from '../../contexts/auth';
import { FiSettings, FiUpload } from 'react-icons/fi';
import * as apiAuth from '../../services/apiRequests';
import { useNavigate } from 'react-router-dom'

export default function Profile(){
  const { user, signOut } = useContext(AuthContext);

  const [nome, setNome] = useState(user && user.nome);
  const [email, setEmail] = useState(user && user.email);
  const [avatarUrl, setAvatarUrl] = useState(user && user.avatarUrl);
  const [selectedFile, setSelectedFile] = useState(0);

  const navigate = useNavigate();

  apiAuth.getLoggedUser().then(data => {
    setNome(data.name);
    setEmail(data.email);
    setAvatarUrl(data.picture);
  });

  async function handleSave(e){
    e.preventDefault();

    apiAuth.postNewImage(selectedFile)
      .then(data => {
        setAvatarUrl(data.picture);
        navigate("/dashboard");
      });

    // apiAuth.putUser({
    //   "name": nome
    // });
  }

  async function runNavigate(){
    navigate("/");
  }

  return(
    <div>
      <Header/>

      <div className="content">
        <Title nome="Meu perfil">
          <FiSettings size={25} />
        </Title>


        <div className="container">
          <form onSubmit={(e)=>handleSave(e)} className="form-profile">
            <label className="label-avatar">
              <span>
                <FiUpload color="#000" size={25} />
              </span>

              <input type="file" id="file" name="file" onChange={(e)=>{setSelectedFile(e.target.files[0])}}/><br/>
              { avatarUrl === null ? 
                <img src={avatarUrl} width="250" height="250" alt="Foto de perfil do usuario" />
                :
                <img src={avatarUrl} width="250" height="250" alt="Foto de perfil do usuario" />
              }
            </label>

            <label>Nome</label>
            <input type="text" value={nome || ""} onChange={ (e) => setNome(e.target.value) } />

            <label>Email</label>
            <input type="text" value={email || ""} disabled={true} />     

            <button type="submit">Salvar</button>
          </form>
        </div>

        <div className="container">
            <button className="logout-btn" onClick={ () => {
              signOut()
                .then(() => {
                  runNavigate();
                });
            } } >
              Sair
            </button>
        </div>

      </div>
    </div>
  )
}
