import { useState, useEffect } from 'react';
import { FiUser, FiDelete,FiEdit2 } from 'react-icons/fi';
import firebase from '../../services/firebaseConnection';
import { toast } from 'react-toastify';
import Header from '../../components/Header';
import Title from '../../components/Title';
import './costumers.css'
import * as apiAuth from '../../services/authentication';

export default function Costumers() {
    const [nome, setNome] = useState('');
    const [cnpj, setCnpj] = useState('');
    const [endereco, setEndereco] = useState('');
    const [clientes, setClientes] = useState([]);

    const userId = "7227ca64-8e82-4054-babb-42db7599613f";

    useEffect(()=>{
      async function loadClientes() {
        apiAuth.getLoggedUser().then(data => {
          console.log(data.clientModels);
        });

        // setClientes();
      }

      loadClientes();
    }, [clientes]);

    function handleSubmit(e){
      e.preventDefault();

      let clientData = [];
      let newClient = {
        "userModelId": userId,
        "name": "",
        "cnpj": "",
        "address": ""
      };

      ((e.target).querySelectorAll("input")).forEach(item => {
        clientData.push(item.value);
      });

      newClient.name = clientData[0];
      newClient.cnpj = clientData[1];
      newClient.address = clientData[2];

      const options = {
        method: "POST",
        headers: {
          "Authorization": "Basic YWRtaW46YWRtaW4=",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(newClient)
      }

      fetch("http://localhost:8080/clientes/criar-cliente", options);
    }

    async function excluir(id){
      const options = {
        method: "DELETE",
        headers: {
          "Authorization": "Basic YWRtaW46YWRtaW4=",
          "Content-Type": "application/json",
        },
      }

      fetch("http://localhost:8080/clientes/deletar-cliente/"+id, options);
    }

    async function atualizar(id){}

    return (
        <div>
            <Header />

            <div className="content">
                <Title nome="Clientes">
                    <FiUser size={25} />
                </Title>


                <div className="container">
                    <form onSubmit={(e)=>{handleSubmit(e)}} className="form-profile costumers">
                        <label>Nome</label>
                        <input placeholder="Digite o Nome Fantasia" type="text" value={nome} onChange={(e) => setNome(e.target.value)} />

                        <label>CNPJ</label>
                        <input placeholder="Digite o CNPJ" type="text" value={cnpj} onChange={(e) => { setCnpj(e.target.value) }} />

                        <label>Endereço</label>
                        <input placeholder="Digite o seu Endereço" type="text" value={endereco} onChange={(e) => { setEndereco(e.target.value) }} />

                        <button className="button-costumers" type="submit">Salvar</button>
                    </form>
                </div>
                <table>
              <thead>
                <tr>
                  <th scope="col">Cliente</th>
                  <th scope="col">CNPJ</th>
                  <th scope="col">Endereço</th>
                  <th scope="col">Cadastrado em</th>
                  <th scope="col">#</th>
                </tr>
              </thead>
              <tbody>
                  {clientes.map((cliente)=>{
                      return(
                        <tr>
                        <td data-label="Cliente">{cliente.nome}</td>
                        <td data-label="CNPJ">{cliente.cnpj}</td>
                        <td data-label="Endereço">{cliente.endereco}</td>
                        <td data-label="Cadastrado">{cliente.timestamp}</td>
                        <td data-label="#">
                          <button onClick={()=>{excluir(cliente.id)}} className="action" style={{backgroundColor: '#3583f6' }}>
                            <FiDelete color="#FFF" size={17} />
                          </button>
                          <button onClick={()=>{atualizar(cliente.id)}} className="action" style={{backgroundColor: '#F6a935' }}>
                            <FiEdit2 color="#FFF" size={17} />
                          </button>
                        </td>
                      </tr>
                      );
                  })}

              </tbody>
            </table>
            </div>
        </div>
    );
}
