import { useState, useContext } from 'react';
import './profile.css';
import Header from '../../components/Header';
import Title from '../../components/Title';
import avatar from '../../assets/avatar.png';
import { AuthContext } from '../../contexts/auth';
import { FiSettings, FiUpload } from 'react-icons/fi';
import firebase from '../../services/firebaseConnection';
import { toast } from 'react-toastify';
import * as apiAuth from '../../services/apiRequests';

export default function Profile(){
  const { user, signOut, setUser, setLocalUser} = useContext(AuthContext);

  const [nome, setNome] = useState(user && user.nome);
  const [email, setEmail] = useState(user && user.email);
  const [avatarUrl, setAvatarUrl] = useState(user && user.avatarUrl);
  const [imageAvatar, setImageAvatar]= useState(user && user.imageAvatar);

  apiAuth.getLoggedUser().then(data => {
    setNome(data.name);
    setEmail(data.email);
    setAvatarUrl(data.picture);
    setImageAvatar(data.picture);
  });

  function handleFile(e){
    handleUpload(e.target.files[0]);
  }

  async function handleSave(e){
    e.preventDefault();
  }

  function handleUpload(files){
    console.log(files);

    const formData = new FormData();

    formData.append("file", files, files.name);

    apiAuth.postProfilePicture(formData);
  }

  return(
    <div>
      <Header/>

      <div className="content">
        <Title nome="Meu perfil">
          <FiSettings size={25} />
        </Title>


        <div className="container">
          <form onSubmit={(e)=>handleSave(e)} className="form-profile">
            <label className="label-avatar">
              <span>
                <FiUpload color="#000" size={25} />
              </span>

              <input type="file" accept="image/*" onChange={handleFile}/><br/>
              { avatarUrl === null ? 
                <img src={avatar} width="250" height="250" alt="Foto de perfil do usuario" />
                :
                <img src={avatarUrl} width="250" height="250" alt="Foto de perfil do usuario" />
              }
            </label>

            <label>Nome</label>
            <input type="text" value={nome} onChange={ (e) => setNome(e.target.value) } />

            <label>Email</label>
            <input type="text" value={email} disabled={true} />     

            <button type="submit">Salvar</button>       

          </form>
        </div>

        <div className="container">
            <button className="logout-btn" onClick={ () => signOut() } >
              Sair
            </button>
        </div>

      </div>
    </div>
  )
}
