import { useState } from "react";

export default function Costumers(){
  const [nome, setNome] = useState("");
  const [cnpj, setCnpj] = useState();
  const [endereco, setEndereco] = useState("");
  const [cliente, setCliente] = useState({});
  const [clientes, setClientes] = useState([]);

  return(
    <div>
      <Header />
      <div className="content">
        <Title nome="Clientes"><FiUser size={25}/></Title>
        <div className="container">
          <form onSubmit={(e)=>{handleSubmit(e)}} className="form-profile costumers">
            <label>Nome</label>
            <input placeholder="Digite o Nome Fantasia" type="text" value={nome || ""} onChange={(e) => setNome(e.target.value)} />

            <label>CNPJ</label>
            <input placeholder="Digite o CNPJ" type="text" value={cnpj || ""} onChange={(e) => { setCnpj(e.target.value) }} />

            <label>Endere√ßo</label>
            <input placeholder="Digite o seu Endere√ßo" type="text" value={endereco || ""} onChange={(e) => { setEndereco(e.target.value) }} />

            <button className="button-costumers" type="submit">Salvar</button>
          </form>
        </div>
        <table>
          <thead>
            <tr>
              <th scope="col">Cliente</th>
              <th scope="col">CNPJ</th>
              <th scope="col">Endere√ßo</th>
              <th scope="col">Cadastrado em</th>
              <th scope="col">#</th>
            </tr>
          </thead>
          <tbody>
            {
              clientes.map(
                (cliente, index) => {
                  return(
                    <tr key={index}>
                      <td data-label="Cliente">{cliente.nome}</td>
                      <td data-label="CNPJ">{cliente.cnpj}</td>
                      <td data-label="Endere√ßo">{cliente.endereco}</td>
                      <td data-label="Cadastrado">{cliente.timestamp}</td>
                      <td data-label="#">
                        <button onClick={()=>{excluir(cliente.id)}} className="action" style={{backgroundColor: '#3583f6' }}>
                          <FiDelete color="#FFF" size={17} />
                        </button>
                        <button onClick={()=>{atualizar(cliente.id)}} className="action" style={{backgroundColor: '#F6a935' }}>
                          <FiEdit2 color="#FFF" size={17} />
                        </button>
                      </td>
                    </tr>
                  );
                }
              )
            }
          </tbody>
        </table>
      </div>
    </div>
  );
}
