import axios from "axios";
import { createNewUser, getUserFirebaseId } from "./authentication";

export function postNewUser(newUser){
  createNewUser(newUser)
    .then((data) => {
      newUser.firebaseId = data.user.uid;

      const options = {
        method: "POST",
        headers: {
          "Authorization": "Basic YWRtaW46YWRtaW4=",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(newUser)
      }

      fetch("http://localhost:8080/usuarios/criar-usuario", options)
    });
}

export function getLoggedUser(){
  const options = {
    method: "GET",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  return getUserFirebaseId()
    .then(data => {
      return fetch("http://localhost:8080/usuarios/login/todos-os-usuarios/"+data, options)
        .then(response => response.json());
    });
}

export function postNewClient(newClient){
  const options = {
    method: "POST",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newClient)
  }

  fetch("http://localhost:8080/clientes/criar-cliente", options);
}

export function postNewCall(newCall){
  const options = {
    method: "POST",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newCall)
  }

  fetch("http://localhost:8080/chamados/criar-chamado", options);
}

export function deleteClient(id){
  const options = {
    method: "DELETE",
    headers: {
      "Authorization": "Basic YWRtaW46YWRtaW4=",
      "Content-Type": "application/json",
    },
  }

  fetch("http://localhost:8080/clientes/deletar-cliente/"+id, options);
}

export function postNewImage(newImage){
  const form = new FormData();

  form.append("file", newImage);

  const options = {
    "method": "POST",
    "headers": {
      "cookie": "JSESSIONID=221B41DFFE80A336611CFB7E99FA1D60",
      "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
      "Authorization": "Basic YWRtaW46YWRtaW4="
    },
    "body": form
  }

  getLoggedUser().then((data) => {
    console.log(data)
    // fetch("http://localhost:8080/usuarios/"+id+"/salvar-imagem", options);
  });
}
