import { createNewUser } from "./authentication";

const userId = "7227ca64-8e82-4054-babb-42db7599613f";

export function postNewUser(newUser){
  let body = {
    "name": "",
    "email": "",
    "password": "",
    "picture": ""
  };

  createNewUser(newUser)
    .then(
      body.name = newUser.name,
      body.email = newUser.email,
      body.password = newUser.password,
      body.picture = newUser.picture,
    );

  console.log(newUser);

